<!doctype html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport"
          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">
    <meta http-equiv="X-UA-Compatible" content="ie=edge">
    <meta name="csrf-token" content="{{ csrf_token() }}">
    <title>Base64 Image Upload</title>
</head>
<body>
<h2>Upload ảnh (Base64)</h2>
<input type="file" id="imageInput" accept="image/*">
<br><br>
<img id="preview" src="" style="max-width: 200px;">
<img src="data:image/jpeg;base64," />

<script>
    document.getElementById('imageInput').addEventListener('change', function () {
        const file = this.files[0];
        if (!file) return;

        const reader = new FileReader();

        reader.onload = function (e) {
            console.log(99999);
            const base64Image = e.target.result;

            // Hiển thị trước
            document.getElementById('preview').src = base64Image;

            // Gửi đến server
            fetch('/upload-base64', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'X-CSRF-TOKEN': document.querySelector('meta[name="csrf-token"]').getAttribute('content')
                },
                body: JSON.stringify({
                    image: base64Image
                })
            })
                .then(response => response.json())
                .then(data => {
                    console.log(data.path, 8);
                    if (data.path) {
                        document.getElementById('preview').src = '/' + data.path;
                    } else {
                        alert("Upload thất bại: " + (data.error || 'Unknown error'));
                    }
                })
                .catch(err => {
                    console.error(err);
                    alert("Lỗi khi upload ảnh");
                });
        };

        reader.readAsDataURL(file); // Convert to base64
    });
</script>
</body>
</html>
